import React, { Component } from 'react';

class App extends Component{

    constructor(){
        super();
        this.state = {displayBio : false};

        this.onRead = this.onRead.bind(this)
    }

    onRead() {
        this.setState({ displayBio: true});
    }


    render(){ 
        return(
            <div>
                <h1>Aditi Gupta</h1>
                <p>My name is Aditi Gupta. I am a jr. associate IT.</p>
                {
                    this.state.displayBio ? (
                        <div>
                            <p> I am persuing Btechf rom MIET, Meerut</p>
                            <p> I done a remote internship from internity in java. they gave 
                                me many challenges on which i stood upon.
                            </p>
                        </div>
                    ) : (
                        <div> <button onClick={this.onRead}>Read More</button> </div>
                    )
                }
            </div>
        )
    }
}
export default App;
