import React, { Component } from 'react';
import Projects from './Projects';
import SocialProfiles from './S'

class App extends Component{

    constructor(){
        super();
        this.state = {displayBio : false};

        this.toggleDisplayBio = this.toggleDisplayBio.bind(this);
    }

    toggleDisplayBio(){
        this.setState({ displayBio: !this.state.displayBio});         
    }

    render(){ 
        return(
            <div>
                <h1>Aditi Gupta</h1>
                <p>My name is Aditi Gupta. I am a jr. associate IT.</p>
                {
                    this.state.displayBio ? (
                        <div>
                            <p> I am persuing Btechf rom MIET, Meerut</p>
                            <p> I done a remote internship from internity in java. they gave 
                                me many challenges on which i stood upon.
                            </p>
                            <button onClick={this.toggleDisplayBio}>Read Less</button>
                        </div>
                    ) : (
                        <div> <button onClick={this.toggleDisplayBio}>Read More</button> </div>
                    )
                }

                <hr />

                <Projects />

            </div>   
        )
    }
}
export default App;
