import React from "react";
import layers from "../img/layers.png";
import cogwheel from "../img/cogwheel.png";
import pen from "../img/pen.png";
import browser from "../img/browser.png";
import myImage from "../img/myImage.jpg";
import api from "../img/api.png";
import PropTypes from "prop-types";

const data = [
  {
    label: 'Responsive',
  },
  {
    label: 'Design',
  },
  {
    label: 'API Integration',
  },
  {
    label: 'Performance',
  },
  {
    label: 'Agile & Scrum',
  },
  {
    label: 'Big Data With Hadoop',
  },
  {
    label: 'Core Java',
  },
  {
    label: 'Project Management',
  },
]
const skills = [
  {
    label: 'React JS',
    progress: '90%',
  },
  {
    label: 'Java Script',
    progress: '80%',
  },
  {
    label: 'HTML5',
    progress: '90%',
  },
  {
    label: 'CSS',
    progress: '90%',
  },
  {
    label: 'Bootstrap',
    progress: '90%',
  },
  {
    label: 'Core Java',
    progress: '90%',
  },
  {
    label: 'node JS',
    progress: '40%',
  },
  {
    label: 'Project Management',
  }
]
export default class About extends React.Component {
  render() {
    return (
      <div
        id="about-container"
        className="content-containers container text-center mt-5">
        <h1 id="about" className={this.props.bounceLeft}>
          About
				</h1>
        <div className="row mt-5">
          {
            data.map((list, index) => (
              <div
                key={index}
                className={
                  "col-6 col-sm-6 col-md-3 " + this.props.fadeInLeft
                }>
                <img src={layers} alt="" />
                <h4>{list.label}</h4>
              </div>
            ))
          }
        </div>

        <div className="row" style={{ marginTop: 7 + "rem" }}>
          <div className="col-12 col-lg-6 hidden">
            <img
              className={"img-fluid " + this.props.fadeIn}
              src={myImage}
              alt=""
              style={{
                borderRadius: '32px',
                width: '206px',
              }}
            />

          </div>
          <div
            className={
              "col-12 col-sm-12 col-md-12 col-lg-6 " +
              this.props.tada
            }>
            <p className={"mytext pt-3 " + this.props.fadeIn}>
              I am seeking a professional opportunity as a front-end
              or full-stack web developer. I love the culture and unique
              challenges in the industry, and I am eager for the chance
              to work professionally as a developer.
						</p>
            <p className={"mytext pt-3 " + this.props.fadeIn}>
              Revealed my passion for problem solving, and creating quality products
              through collaborative teamwork. I specialize in HTML, CSS, JavaScript,
              and React on the front-end as well as know basics of Core Java, Node and
              MySQL/Mongo on the back-end to create beautiful and secure applications.
              My biggest priority is developing clear, high-quality code while
              cultivating a fun and encouraging workplace culture.
            </p>
          </div>
        </div>
        <div style={{ margin: '16px', marginTop: '7rem' }} className='row'>
          <div
            className="col-12 col-lg-6 hidden"
            style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', fontWeight: 'bold' }}>
            <div style={{ background: 'black', color: 'white', width: '50%', borderRadius: '50px', margin: '16px' }}>
              <div style={{ padding: '18px 20%' }}>
              Skills
              </div>
            </div>
          </div>
          <div
            className={
              "col-12 col-sm-12 col-md-12 col-lg-6 " +
              this.props.tada
            }>

            <div className="progress mb-3">
              <div
                className="progress-bar fill-80-bar"
                role="progressbar"
                style={{ width: 80 + "%" }}
                aria-valuemin="0"
                aria-valuemax="100">
                HTML5
							</div>
            </div>
            <div className="progress mb-3">
              <div
                className="progress-bar fill-80-bar"
                role="progressbar"
                style={{ width: 80 + "%" }}
                aria-valuemin="0"
                aria-valuemax="100">
                CSS3
							</div>
            </div>
            <div className="progress mb-3">
              <div
                className="progress-bar fill-90-bar"
                role="progressbar"
                style={{ width: 90 + "%" }}
                aria-valuemin="0"
                aria-valuemax="100">
                Javascript
							</div>
            </div>
            <div className="progress mb-3">
              <div
                className="progress-bar fill-60-bar"
                role="progressbar"
                style={{ width: 60 + "%" }}
                aria-valuemin="0"
                aria-valuemax="100">
                PHP
							</div>
            </div>
            <div className="progress mb-3">
              <div
                className="progress-bar fill-80-bar"
                role="progressbar"
                style={{ width: 80 + "%" }}
                aria-valuemin="0"
                aria-valuemax="100">
                ReactJS
							</div>
            </div>
            <div className="progress mb-3">
              <div
                className="progress-bar fill-60-bar"
                role="progressbar"
                style={{ width: 60 + "%" }}
                aria-valuemin="0"
                aria-valuemax="100">
                NodeJs
							</div>
            </div>
            <div className="progress mb-3">
              <div
                className="progress-bar fill-60-bar"
                role="progressbar"
                style={{ width: 60 + "%" }}
                aria-valuemin="0"
                aria-valuemax="100">
                SASS
							</div>
            </div>
            <div className="progress mb-3">
              <div
                className="progress-bar fill-80-bar"
                role="progressbar"
                style={{ width: 80 + "%" }}
                aria-valuemin="0"
                aria-valuemax="100">
                VanillaJS
							</div>
            </div>
            <div className="progress mb-3">
              <div
                className="progress-bar fill-60-bar"
                role="progressbar"
                style={{ width: 60 + "%" }}
                aria-valuemin="0"
                aria-valuemax="100">
                JQuery
							</div>

            </div>
          </div>
        </div>
      </div>
    );
  }
}

About.propTypes = {
  bounceLeft: PropTypes.string,
  fadeInLeft: PropTypes.string,
  fadeInRight: PropTypes.string,
  fadeIn: PropTypes.string,
  tada: PropTypes.string
};
